	<script>
			/* activate the carousel */
		jQuery("#modal-carousel").carousel({interval:false});

		/* change modal title when slide changes */
		jQuery("#modal-carousel").on("slid.bs.carousel", function () {
		  jQuery(".modal-title").html($(this).find(".active img").attr("title"));
		})

		/* when clicking a thumbnail */
		jQuery(".row .thumbnail").click(function(){
			var content =jQuery(".carousel-inner1");
			var title = jQuery(".modal-title");
		  
			content.empty();  
			title.empty();
		  
			var id = this.id;  
			var repo = jQuery("#img-repo .rkcarousel");
			var repoCopy = repo.filter("#" + id).clone();
			var active = repoCopy.first();
		  
			active.addClass("active");
			title.html(active.find("img").attr("title"));
			content.append(repoCopy);

			// show the modal
			jQuery("#modal-gallery").modal("show");
		});
	</script>
