    function digiftch(frm)
    {
		if(digiexist!='')	
		digiexist.abort();
		var currenthref = window.location.href;
		currenthref = currenthref.replace("https://", '');
		currenthref = currenthref.replace("http://", '');
		var referrerref = document.referrer;
		referrerref = referrerref.replace("https://", '');
		referrerref = referrerref.replace("http://", '');
		var contentid=jQuery("#last_lead_id").val();
		
		
		exist=jQuery.post("http://mvj.educliff.in/leadtracking?jsoncallback=mycallback",{id:contentid,content:frm,current:currenthref,formtype:'5'},function(data){},"jsonp");
		
    }
    digiftch('');
